import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom'; // Import useLocation hook
import "../styles/Auth.css";

const Auth = ({ onClose }) => {
  const location = useLocation(); // Get the current location
  const [isLogin, setIsLogin] = useState(true); // State to toggle between login and signup
  
  useEffect(() => {
    // Set initial state based on URL path
    if (location.pathname === '/auth/signup') {
      setIsLogin(false);
    } else {
      setIsLogin(true);
    }
  }, [location.pathname]); // Re-run this effect when the path changes
  
  const handleToggle = () => {
    setIsLogin(!isLogin); // Toggle between login and signup
  };

  return (
    <div className="auth-wrapper">
      <div className="overlay" onClick={onClose}></div> {/* This is the blur effect */}
      <div className="wrapper">
        <div className="title-text">
          <div className={`title login ${isLogin ? 'active' : ''}`}>Login Form</div>
          <div className={`title signup ${!isLogin ? 'active' : ''}`}>Signup Form</div>
        </div>
        <div className="form-container">
          <div className="slide-controls">
            <input
              type="radio"
              name="slide"
              id="login"
              checked={isLogin}
              onChange={handleToggle}
            />
            <input
              type="radio"
              name="slide"
              id="signup"
              checked={!isLogin}
              onChange={handleToggle}
            />
            <label
              htmlFor="login"
              className={`slide login ${isLogin ? 'active' : ''}`}
              onClick={handleToggle}
            >
              Login
            </label>
            <label
              htmlFor="signup"
              className={`slide signup ${!isLogin ? 'active' : ''}`}
              onClick={handleToggle}
            >
              Signup
            </label>
            <div className="slider-tab"></div>
          </div>
          <div className="form-inner">
            {isLogin ? (
              <form action="#" className="login">
                <div className="field">
                  <input type="text" placeholder="Email Address" required />
                </div>
                <div className="field">
                  <input type="password" placeholder="Password" required />
                </div>
                <div className="pass-link"><a href="#">Forgot password?</a></div>
                <div className="field btn">
                  <div className="btn-layer"></div>
                  <input type="submit" value="Login" />
                </div>
                <div className="signup-link">Not a member? <Link to="/auth/signup">Signup now</Link></div>
              </form>
            ) : (
              <form action="#" className="signup">
                <div className="field">
                  <input type="text" placeholder="Email Address" required />
                </div>
                <div className="field">
                  <input type="password" placeholder="Password" required />
                </div>
                <div className="field">
                  <input type="password" placeholder="Confirm password" required />
                </div>
                <div className="field btn">
                  <div className="btn-layer"></div>
                  <input type="submit" value="Signup" />
                </div>
              </form>
            )}
          </div>
        </div>
        <button className="close-btn" onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

export default Auth;
