import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min";
import "../styles/Header.css";
import "bootstrap-icons/font/bootstrap-icons.css";
import { Link } from "react-router-dom";
import Auth from "./Auth";
import { Modal } from "react-bootstrap";

const Header = () => {
  const [showModal, setShowModal] = useState(false);

  const handleOpenModal = () => setShowModal(true);
  const handleCloseModal = () => setShowModal(false);

  return (
    <div>
      {/* ---Header--- */}
      <nav
        className="navbar navbar-expand-lg navbar-light"
        style={{ background: "#479761" }}
      >
        <div className="container-fluid">
          {/* Logo Section */}
          <a
            className="navbar-brand d-flex align-items-center me-auto text-nowrap"
            href="/"
            style={{ color: "white", fontWeight: "bold", fontSize: "1.2rem" }}
          >
            <i className="bi bi-flower2 me-2"></i> MyLogo
          </a>

          {/* Navbar Toggler for Mobile View */}
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          {/* Navbar Content */}
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0 d-flex align-items-center">
              <li className="nav-item mx-2">
                <Link className="nav-link text-nowrap" to="/">
                  <i className="bi bi-house-fill me-1"></i> Home
                </Link>
              </li>
              {/* Other Nav Links */}
              <li className="nav-item mx-2">
                <span
                  className="nav-link text-nowrap"
                  style={{ cursor: "pointer" }}
                  onClick={handleOpenModal}
                >
                  <i className="bi bi-box-arrow-in-right me-1"></i> Login / Signup
                </span>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {/* Modal for Login/Signup */}
      <Modal show={showModal} onHide={handleCloseModal} centered>
        <Modal.Header closeButton>
          <Modal.Title>{/* Title can dynamically change */}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Auth />
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default Header;
