import React, { useState } from "react";
import { Form, Button, InputGroup, Dropdown } from "react-bootstrap";
import "../styles/SearchHeader.css";

const SearchHeader = () => {
  const [view, setView] = useState("grid");
  const [sort, setSort] = useState("Sort by: None");

  const handleSort = (type) => {
    setSort(type);
  };

  return (
    <div className="search-header py-3 px-4 bg-light mt-4">
      <div className="d-flex flex-column flex-md-row justify-content-between align-items-center">
        {/* Left Section */}
        <div>
          <h4 className="fw-bold">Wedding Venues in Chennai</h4>
          <p className="text-muted mb-0">
            Showing <span className="fw-bold">1533</span> results as per your
            search criteria
          </p>
        </div>

        {/* Right Section */}
        <div className="d-flex align-items-center mt-3 mt-md-0">

          {/* Search Box */}
          <InputGroup className="search-box me-3">
            <InputGroup.Text>
              <i className="bi bi-search"></i>
            </InputGroup.Text>
            <Form.Control placeholder="Search Wedding Venues..." />
          </InputGroup>

          {/* List/Grid Toggle */}
          <div className="d-flex align-items-center toggle-view">
            <i
              className={`bi bi-list-ul me-2 ${view === "list" ? "active" : ""}`}
              onClick={() => setView("list")}
            ></i>
            <span className={`me-3 ${view === "list" ? "text-primary" : ""}`}>
              List
            </span>
            <i
              className={`bi bi-grid-3x3-gap-fill me-2 ${
                view === "grid" ? "active" : ""
              }`}
              onClick={() => setView("grid")}
            ></i>
            <span className={`${view === "grid" ? "text-primary" : ""}`}>
              Grid
            </span>
          </div>
        </div>
      </div>

      {/* Sorting Options */}
      <div className="mt-3 d-flex align-items-center">
        <Dropdown onSelect={handleSort}>
          <Dropdown.Toggle variant="light" id="dropdown-sort">
            {sort}
          </Dropdown.Toggle>
          <Dropdown.Menu>
            <Dropdown.Item eventKey="Sort by: Price (Low to High)">
              Price (Low to High)
            </Dropdown.Item>
            <Dropdown.Item eventKey="Sort by: Price (High to Low)">
              Price (High to Low)
            </Dropdown.Item>
            <Dropdown.Item eventKey="Sort by: Rating (High to Low)">
              Rating (High to Low)
            </Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
      </div>
    </div>
  );
};

export default SearchHeader;
