import React, { useState, useEffect } from "react";
import "../styles/Auth.css";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Auth = ({ onAuthSuccess }) => {
  const [isLogin, setIsLogin] = useState(true); // State to toggle between login and signup
  const [isForgotPassword, setIsForgotPassword] = useState(false); // State to show forgot password form
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [isVerification, setIsVerification] = useState(false); // Verification state
  const [verificationCode, setVerificationCode] = useState('');

  const handleToggle = () => {
    setIsLogin(!isLogin); // Toggle between login and signup
    setIsVerification(false); // Reset verification state when toggling between login and signup
  };

  const handleForgotPassword = () => {
    setIsForgotPassword(true); // Show forgot password form
  };

  const handleBackToLogin = () => {
    setIsForgotPassword(false); // Return to login form
    setIsVerification(false); // Reset verification state
  };

  useEffect(() => {
    console.log("isVerification State Changed: ", isVerification);
  }, [isVerification]);

  const handleSignup = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/auth/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password, confirmPassword }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Please check the details entered');
      }

      const data = await response.json();
      toast.success('Your Account has been created successfully!');
      setIsVerification('sendCode');
    } catch (err) {
      toast.error(err.message);
    }
  };

  const handleSignupSubmit = (e) => {
    e.preventDefault();
    if (!email || !password || !confirmPassword) {
      toast.error('Please check the details entered');
      return;
    }
    handleSignup();
  };

  const handleLogin = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/auth/signin', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Invalid email or Password');
      }

      const data = await response.json();
      localStorage.setItem('token', data.token);
      localStorage.setItem('email', email);

      toast.success('Login Successful!!');
      onAuthSuccess(email);
    } catch (err) {
      toast.error(err.message);
    }
  };

  const handleSendCode = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/auth/send-verification-code', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Error in sending verification code');
      }

      toast.success('Verification code sent successfully!!');
      setIsVerification('codeInput');
    } catch (err) {
      toast.error(err.message);
    }
  };

  const handleVerifyCode = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/auth/verify-verification-code', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, providedCode: verificationCode }),
      });
  
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Invalid verification code');
      }
  
      toast.success('Verification successful!');
      // Redirect to home or trigger a callback
      onAuthSuccess(email);
    } catch (err) {
      toast.error(err.message);
    }
  };
  

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!email || !password) {
      toast.error('Email and Password are required!');
      return;
    }
    handleLogin();
  };

  return (
    <div className="auth-wrapper">
      <div className="wrapper">
        <div className="title-text">
          {/* Show the appropriate title */}
          {!isVerification && !isForgotPassword ? (
            <>
              <div className={`title login ${isLogin ? "active" : ""}`}>Login Form</div>
              <div className={`title signup ${!isLogin ? "active" : ""}`}>Signup Form</div>
            </>
          ) : (
            <div className="title">{isForgotPassword ? "Forgot Password" : "Email Verification"}</div>
          )}
        </div>

        <div className="form-container">
  {isForgotPassword ? (
    <div className="form-inner">
      <form action="#" className="forgot-password">
        <div className="field">
          <input type="email" placeholder="Enter your Email" required />
        </div>
        <div className="field btn">
          <button type="button" className="btn btn-primary">
            Send Code
          </button>
        </div>
        <div className="pass-link">
          <a href="#" onClick={handleBackToLogin}>
            Back to Login
          </a>
        </div>
      </form>
    </div>
  ) : isVerification === 'sendCode' ? (
    <div className="form-inner">
      <form action="#" className="verify-email">
        <div className="field">
          <input type="email" placeholder="Email Address" required value={email} disabled />
        </div>
        <div className="field">
          <button type="button" className="btn btn-primary" onClick={handleSendCode}>
            Send Code
          </button>
        </div>
        <div className="pass-link">
          <a href="#" onClick={handleBackToLogin}>
            Back to Login
          </a>
        </div>
      </form>
    </div>
  ) : isVerification === 'codeInput' ? (
    <div className="form-inner">
      <form action="#" className="verify-code">
        <div className="field">
          <input
            type="text"
            placeholder="Enter Verification Code"
            required
            onChange={(e) => setVerificationCode(e.target.value)}
          />
        </div>
        <div className="field btn">
          <button type="button" className="btn btn-primary" onClick={handleVerifyCode}>
            Verify Code
          </button>
        </div>
      </form>
    </div>
  ) : (
    <>
      <div className="slide-controls">
        <input
          type="radio"
          name="slide"
          id="login"
          checked={isLogin}
          onChange={handleToggle}
        />
        <input
          type="radio"
          name="slide"
          id="signup"
          checked={!isLogin}
          onChange={handleToggle}
        />
        <label
          htmlFor="login"
          className={`slide login ${isLogin ? 'active' : ''}`}
          onClick={handleToggle}
        >
          Login
        </label>
        <label
          htmlFor="signup"
          className={`slide signup ${!isLogin ? 'active' : ''}`}
          onClick={handleToggle}
        >
          Signup
        </label>
        <div className="slider-tab"></div>
      </div>

      <div className="form-inner">
        {isLogin ? (
          <form action="#" className="login" onSubmit={handleSubmit}>
            <div className="field">
              <input
                type="text"
                placeholder="Email Address"
                required
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className="field">
              <input
                type="password"
                placeholder="Password"
                required
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <div className="pass-link">
              <a href="#" onClick={handleForgotPassword}>
                Forgot password?
              </a>
            </div>
            <div className="field btn">
              <button type="submit" className="btn btn-primary">
                Login
              </button>
            </div>
          </form>
        ) : (
          <form action="#" className="signup" onSubmit={handleSignupSubmit}>
            <div className="field">
              <input
                type="email"
                placeholder="Email Address"
                required
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className="field">
              <input
                type="password"
                placeholder="Password"
                required
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <div className="field">
              <input
                type="password"
                placeholder="Confirm password"
                required
                onChange={(e) => setConfirmPassword(e.target.value)}
              />
            </div>
            <div className="field btn">
              <button type="submit" className="btn btn-primary">
                Signup
              </button>
            </div>
          </form>
        )}
      </div>
    </>
  )}
</div>

      </div>
      <ToastContainer />
    </div>
  );
};

export default Auth;
