import React, { useState, useEffect } from "react";
import "../styles/Auth.css";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const Auth = ({ onAuthSuccess }) => {
  const [isLogin, setIsLogin] = useState(true); // Login or Signup state
  const [isForgotPassword, setIsForgotPassword] = useState(false); // Forgot password state
  const [isVerification, setIsVerification] = useState(false); // Email verification state
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const handleToggle = () => {
    setIsLogin(!isLogin); // Toggle between login and signup
    setIsVerification(false);
    setIsForgotPassword(false);
  };

  const handleForgotPassword = () => {
    setIsForgotPassword(true); // Show forgot password form
    setIsLogin(false); // Exit login mode
    setIsVerification(false);
  };

  const handleBackToLogin = () => {
    setIsForgotPassword(false); // Return to login form
    setIsVerification(false);
    setIsLogin(true);
  };

  const handleSignup = async () => {
    try {
      const response = await fetch("http://localhost:8000/api/auth/signup", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password, confirmPassword }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Please check the details entered.");
      }

      toast.success("Your account has been created successfully!");
      setIsVerification(true);
      onAuthSuccess(email);
    } catch (err) {
      toast.error(err.message);
    }
  };

  const handleLogin = async () => {
    try {
      const response = await fetch("http://localhost:8000/api/auth/signin", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Invalid email or password.");
      }

      const data = await response.json();
      localStorage.setItem("token", data.token);
      localStorage.setItem("email", email);
      toast.success("Login Successful!");
      onAuthSuccess(email);
    } catch (err) {
      toast.error(err.message);
    }
  };

  const handleSendCode = async () => {
    try {
      const response = await fetch("http://localhost:8000/api/auth/send-verification-code", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Error in sending verification code.");
      }

      toast.success("Verification code sent successfully!");
    } catch (err) {
      toast.error(err.message);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isLogin) {
      if (!email || !password) {
        toast.error("Email and password are required!");
        return;
      }
      handleLogin();
    } else {
      if (!email || !password || !confirmPassword) {
        toast.error("Please check the details entered.");
        return;
      }
      handleSignup();
    }
  };

  return (
    <div className="auth-wrapper">
      <div className="wrapper">
        <div className="title-text">
          {!isVerification && !isForgotPassword ? (
            <>
              <div className={`title login ${isLogin ? "active" : ""}`}>Login Form</div>
              <div className={`title signup ${!isLogin ? "active" : ""}`}>Signup Form</div>
            </>
          ) : (
            <div className="title">
              {isForgotPassword ? "Forgot Password" : "Email Verification"}
            </div>
          )}
        </div>

        <div className="form-container">
          {isForgotPassword ? (
            <div className="form-inner">
              <form className="forgot-password">
                <div className="field">
                  <input
                    type="email"
                    placeholder="Enter your Email"
                    required
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </div>
                <div className="field btn">
                  <button type="button" className="btn btn-primary" onClick={handleSendCode}>
                    Send Code
                  </button>
                </div>
                <div className="pass-link">
                  <a href="#" onClick={handleBackToLogin}>
                    Back to Login
                  </a>
                </div>
              </form>
            </div>
          ) : isVerification ? (
            <div className="form-inner">
              <form className="verify-email">
                <div className="field">
                  <input type="email" placeholder="Email Address" value={email} disabled />
                </div>
                <div className="field">
                  <button type="button" className="btn btn-primary" onClick={handleSendCode}>
                    Send Code
                  </button>
                </div>
                <div className="pass-link">
                  <a href="#" onClick={handleBackToLogin}>
                    Back to Login
                  </a>
                </div>
              </form>
            </div>
          ) : (
            <>
              <div className="slide-controls">
                <input
                  type="radio"
                  name="slide"
                  id="login"
                  checked={isLogin}
                  onChange={handleToggle}
                />
                <input
                  type="radio"
                  name="slide"
                  id="signup"
                  checked={!isLogin}
                  onChange={handleToggle}
                />
                <label
                  htmlFor="login"
                  className={`slide login ${isLogin ? "active" : ""}`}
                  onClick={handleToggle}
                >
                  Login
                </label>
                <label
                  htmlFor="signup"
                  className={`slide signup ${!isLogin ? "active" : ""}`}
                  onClick={handleToggle}
                >
                  Signup
                </label>
                <div className="slider-tab"></div>
              </div>

              <div className="form-inner">
                <form onSubmit={handleSubmit}>
                  <div className="field">
                    <input
                      type="email"
                      placeholder="Email Address"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      required
                    />
                  </div>
                  <div className="field">
                    <input
                      type="password"
                      placeholder="Password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      required
                    />
                  </div>
                  {!isLogin && (
                    <div className="field">
                      <input
                        type="password"
                        placeholder="Confirm password"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        required
                      />
                    </div>
                  )}
                  <div className="field btn">
                    <button type="submit" className="btn btn-primary">
                      {isLogin ? "Login" : "Signup"}
                    </button>
                  </div>
                </form>
                {isLogin && (
                  <div className="pass-link">
                    <a href="#" onClick={handleForgotPassword}>
                      Forgot password?
                    </a>
                  </div>
                )}
              </div>
            </>
          )}
        </div>
      </div>
      <ToastContainer />
    </div>
  );
};

export default Auth;
