import React, { useState, useEffect } from 'react';
import "../styles/Auth.css";

const Auth = ({ authType = "login", onClose }) => {
  const [isLogin, setIsLogin] = useState(authType === "login");

  useEffect(() => {
    setIsLogin(authType === "login");
  }, [authType]);

  const handleToggle = () => {
    setIsLogin(!isLogin);
  };

  return (
    <div className="auth-modal">
      <div className="overlay" onClick={onClose}></div>
      <div className="wrapper">
        <button className="close-btn" onClick={onClose}>&times;</button>
        <div className="title-text">
          <div className={`title login ${isLogin ? 'active' : ''}`}>Login Form</div>
          <div className={`title signup ${!isLogin ? 'active' : ''}`}>Signup Form</div>
        </div>
        <div className="form-container">
          <div className="slide-controls">
            <label
              htmlFor="login"
              className={`slide login ${isLogin ? 'active' : ''}`}
              onClick={handleToggle}
            >
              Login
            </label>
            <label
              htmlFor="signup"
              className={`slide signup ${!isLogin ? 'active' : ''}`}
              onClick={handleToggle}
            >
              Signup
            </label>
            <div className="slider-tab" style={{ left: isLogin ? '0%' : '50%' }}></div>
          </div>
          <div className="form-inner">
            {isLogin ? (
              <form className="login">
                <div className="field">
                  <input type="text" placeholder="Email Address" required />
                </div>
                <div className="field">
                  <input type="password" placeholder="Password" required />
                </div>
                <div className="pass-link"><a href="#">Forgot password?</a></div>
                <div className="field btn">
                  <div className="btn-layer"></div>
                  <input type="submit" value="Login" />
                </div>
              </form>
            ) : (
              <form className="signup">
                <div className="field">
                  <input type="text" placeholder="Email Address" required />
                </div>
                <div className="field">
                  <input type="password" placeholder="Password" required />
                </div>
                <div className="field">
                  <input type="password" placeholder="Confirm Password" required />
                </div>
                <div className="field btn">
                  <div className="btn-layer"></div>
                  <input type="submit" value="Signup" />
                </div>
              </form>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Auth;
