import React, { useState } from "react";
import { ButtonGroup, Button, InputGroup, Form } from "react-bootstrap";
import "../styles/SearchHeader.css";

const SearchHeader = () => {
  const [priceRange, setPriceRange] = useState([120, 1187500]);
  const [sortOption, setSortOption] = useState("popularity");

  const handlePriceChange = (value) => {
    setPriceRange([value, priceRange[1]]);
  };

  const handleSortChange = (option) => {
    setSortOption(option);
    console.log("Sorting by:", option);
  };

  return (
    <div className="search-header-container">
      <div className="search-header py-3 px-4">
        <div className="d-flex flex-column flex-md-row justify-content-between align-items-center">
          {/* Left Section */}
          <div>
            <h4 className="fw-bold">Wedding Venues in Chennai</h4>
            <p className="text-muted mb-0">
              Showing <span className="fw-bold">1533</span> results as per your
              search criteria
            </p>
          </div>

          {/* Sorting Buttons */}
          <div className="mt-3 mt-md-0">
            <h6 className="mb-2">Sort By</h6>
            <ButtonGroup>
              <Button
                variant={sortOption === "popularity" ? "primary" : "outline-primary"}
                onClick={() => handleSortChange("popularity")}
              >
                Popularity
              </Button>
              <Button
                variant={sortOption === "price_low_high" ? "primary" : "outline-primary"}
                onClick={() => handleSortChange("price_low_high")}
              >
                Price: Low to High
              </Button>
              <Button
                variant={sortOption === "price_high_low" ? "primary" : "outline-primary"}
                onClick={() => handleSortChange("price_high_low")}
              >
                Price: High to Low
              </Button>
            </ButtonGroup>
          </div>

          {/* Price Range Filter */}
          <div className="mt-4">
            <h6 className="mb-2">Price</h6>
            <div className="price-slider-container">
              {/* Start Amount */}
              <div className="slider-label-left">₹{priceRange[0]}</div>
              {/* Slider */}
              <input
                type="range"
                min="120"
                max="1187500"
                value={priceRange[0]}
                onChange={(e) => handlePriceChange(Number(e.target.value))}
                className="price-slider"
              />
              {/* End Amount */}
              <div className="slider-label-right">₹{priceRange[1]}</div>
            </div>
          </div>

          {/* Search Box */}
          <div className="d-flex align-items-center mt-3 mt-md-0">
            <InputGroup className="search-box me-3">
              <InputGroup.Text>
                <i className="bi bi-search"></i>
              </InputGroup.Text>
              <Form.Control placeholder="Search Wedding Venues..." />
            </InputGroup>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SearchHeader;
