import React, { useState } from "react";
import "../styles/Auth.css";
import { API_URL } from "../utils/constants";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Auth = () => {
  const [isLogin, setIsLogin] = useState(true); // State to toggle between login and signup
  const [isForgotPassword, setIsForgotPassword] = useState(false); // State to show forgot password form
  const [username,setUsername] = useState('');
  const [password,setPassword] = useState('');

  const handleToggle = () => {
    setIsLogin(!isLogin); // Toggle between login and signup
  };

  const handleForgotPassword = () => {
    setIsForgotPassword(true); // Show forgot password form
  };

  const handleBackToLogin = () => {
    setIsForgotPassword(false); // Return to login form
  };

  const handleLogin=async()=>{
    try{
      const response=await fetch(API_URL+'auth/login',{
        method:'POST',
        headers:{
          'Content-Type':'application/json',
        },
        body:JSON.stringify({username,password}),
      });
      if(!response.ok){
        const errorData=await response.json();
        throw new Error(errorData.message || 'Invalid Username or Password');
      }
      const data=await response.json();

      localStorage.setItem('token', data.token);
      localStorage.setItem('username',username);

      toast.success('Login Successful!!');
    }
    catch(err){
      toast.error(err.message);
    }
  };

  const handleSubmit = (e)=>{
    e.preventDefault();
    handleLogin();
  };

  return (
    <div className="auth-wrapper">
      <div className="wrapper">
        <div className="title-text">
          {isForgotPassword ? (
            <div className="title">Forgot Password</div>
          ) : (
            <>
              <div className={`title login ${isLogin ? "active" : ""}`}>Login Form</div>
              <div className={`title signup ${!isLogin ? "active" : ""}`}>Signup Form</div>
            </>
          )}
        </div>

        <div className="form-container">
          {isForgotPassword ? (
            <div className="form-inner">
              <form action="#" className="forgot-password">
                <div className="field">
                  <input type="email" placeholder="Enter your Email" required />
                </div>
                <div className="field btn">
                  <button type="button" className="btn btn-primary">
                    Send Code
                  </button>
                </div>
                <div className="pass-link">
                  <a href="#" onClick={handleBackToLogin}>
                    Back to Login
                  </a>
                </div>
              </form>
            </div>
          ) : (
            <>
              <div className="slide-controls">
                <input
                  type="radio"
                  name="slide"
                  id="login"
                  checked={isLogin}
                  onChange={handleToggle}
                />
                <input
                  type="radio"
                  name="slide"
                  id="signup"
                  checked={!isLogin}
                  onChange={handleToggle}
                />
                <label
                  htmlFor="login"
                  className={`slide login ${isLogin ? "active" : ""}`}
                  onClick={handleToggle}
                >
                  Login
                </label>
                <label
                  htmlFor="signup"
                  className={`slide signup ${!isLogin ? "active" : ""}`}
                  onClick={handleToggle}
                >
                  Signup
                </label>
                <div className="slider-tab"></div>
              </div>

              <div className="form-inner">
                {isLogin ? (
                  <form action="#" className="login" onSubmit={handleSubmit}>
                    <div className="field">
                      <input type="text" placeholder="Email Address" required onChange={(e)=>setUsername(e.target.value)}/>
                    </div>
                    <div className="field">
                      <input type="password" placeholder="Password" required onChange={(e)=>setPassword(e.target.value)}/>
                    </div>
                    <div className="pass-link">
                      <a href="#" onClick={handleForgotPassword}>
                        Forgot password?
                      </a>
                    </div>
                    <div className="field btn">
                  <button type="button" className="btn btn-primary">
                    Login
                  </button>
                </div>
                    <div className="field btn">
                      <input type="submit" value="Login" />
                    </div>
                  </form>
                ) : (
                  <form action="#" className="signup">
                    <div className="field">
                      <input type="text" placeholder="Email Address" required />
                    </div>
                    <div className="field">
                      <input type="password" placeholder="Password" required />
                    </div>
                    <div className="field">
                      <input type="password" placeholder="Confirm password" required />
                    </div>
                    <div className="field btn">
                      <input type="submit" value="Signup" />
                    </div>
                    <div className="field btn" style={{marginTop:"-0px"}}>
                  <button type="button" className="btn btn-primary">
                    Signup
                  </button>
                </div>
                  </form>
                )}
              </div>
            </>
          )}
        </div>
      </div>
      <ToastContainer />
    </div>
  );
};

export default Auth;
