import React, { useState, useEffect } from "react";
import "../styles/Auth.css";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const Auth = ({ onAuthSuccess }) => {
  const [view, setView] = useState("login"); // Views: login, signup, forgotPassword, verification
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  useEffect(() => {
    console.log("Current View:", view);
  }, [view]);

  const resetFields = () => {
    setEmail("");
    setPassword("");
    setConfirmPassword("");
  };

  const switchView = (newView) => {
    setView(newView);
    resetFields();
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (view === "signup") {
        if (password !== confirmPassword) {
          toast.error("Passwords do not match!");
          return;
        }
        const response = await fetch("http://localhost:8000/api/auth/signup", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ email, password, confirmPassword }),
        });
        if (!response.ok) throw new Error("Signup failed!");
        toast.success("Signup successful! Verification email sent.");
        switchView("verification");
      } else if (view === "login") {
        const response = await fetch("http://localhost:8000/api/auth/signin", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ email, password }),
        });
        if (!response.ok) throw new Error("Login failed!");
        const data = await response.json();
        localStorage.setItem("token", data.token);
        toast.success("Login successful!");
        onAuthSuccess(email);
      } else if (view === "forgotPassword") {
        const response = await fetch(
          "http://localhost:8000/api/auth/forgot-password",
          {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ email }),
          }
        );
        if (!response.ok) throw new Error("Failed to send reset instructions!");
        toast.success("Reset instructions sent!");
      }
    } catch (err) {
      toast.error(err.message);
    }
  };

  const renderForm = () => {
    switch (view) {
      case "signup":
        return (
          <>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Confirm Password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
            />
          </>
        );
      case "login":
        return (
          <>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </>
        );
      case "forgotPassword":
        return (
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        );
      default:
        return null;
    }
  };

  return (
    <div className="auth-wrapper">
      <div className="form-container">
        <h2>{view === "login" ? "Login" : view === "signup" ? "Signup" : "Forgot Password"}</h2>
        <form onSubmit={handleSubmit}>
          {renderForm()}
          <button type="submit">
            {view === "login"
              ? "Login"
              : view === "signup"
              ? "Signup"
              : "Send Reset Instructions"}
          </button>
        </form>
        <div className="switch-link">
          {view === "login" && (
            <a href="#" onClick={() => switchView("signup")}>
              Create an account
            </a>
          )}
          {view === "signup" && (
            <a href="#" onClick={() => switchView("login")}>
              Back to Login
            </a>
          )}
          {view === "login" && (
            <a href="#" onClick={() => switchView("forgotPassword")}>
              Forgot Password?
            </a>
          )}
        </div>
      </div>
      <ToastContainer />
    </div>
  );
};

export default Auth;
