import React, { useState } from "react";
import "../styles/Auth.css";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

toast.configure();

const Auth = ({ onLoginSuccess }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [username, setUsername] = useState("");

  const handleToggle = () => {
    setIsLogin(!isLogin);
    setEmail("");
    setPassword("");
    setConfirmPassword("");
    setUsername("");
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:8000/api/auth/signin", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });
      const data = await response.json();
      if (response.ok) {
        localStorage.setItem("token", data.token);
        localStorage.setItem("username", data.username); // Assume API returns username
        toast.success("Login successful!");
        onLoginSuccess(data.username);
      } else {
        toast.error(data.message || "Login failed!");
      }
    } catch (error) {
      toast.error("An error occurred!");
    }
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      toast.error("Passwords do not match!");
      return;
    }
    try {
      const response = await fetch("http://localhost:8000/api/auth/signup", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, email, password }),
      });
      const data = await response.json();
      if (response.ok) {
        toast.success("Signup successful! Please login.");
        setIsLogin(true);
      } else {
        toast.error(data.message || "Signup failed!");
      }
    } catch (error) {
      toast.error("An error occurred!");
    }
  };

  return (
    <div className="auth-wrapper">
      <div className="wrapper">
        <div className="title-text">
          <div className={`title login ${isLogin ? "active" : ""}`}>Login Form</div>
          <div className={`title signup ${!isLogin ? "active" : ""}`}>Signup Form</div>
        </div>

        <div className="form-container">
          <div className="slide-controls">
            <input
              type="radio"
              name="slide"
              id="login"
              checked={isLogin}
              onChange={handleToggle}
            />
            <input
              type="radio"
              name="slide"
              id="signup"
              checked={!isLogin}
              onChange={handleToggle}
            />
            <label
              htmlFor="login"
              className={`slide login ${isLogin ? "active" : ""}`}
              onClick={handleToggle}
            >
              Login
            </label>
            <label
              htmlFor="signup"
              className={`slide signup ${!isLogin ? "active" : ""}`}
              onClick={handleToggle}
            >
              Signup
            </label>
            <div className="slider-tab"></div>
          </div>

          <div className="form-inner">
            {isLogin ? (
              <form onSubmit={handleLogin} className="login">
                <div className="field">
                  <input
                    type="email"
                    placeholder="Email Address"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </div>
                <div className="field">
                  <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </div>
                <div className="field btn">
                  <input type="submit" value="Login" />
                </div>
              </form>
            ) : (
              <form onSubmit={handleSignup} className="signup">
                <div className="field">
                  <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                  />
                </div>
                <div className="field">
                  <input
                    type="email"
                    placeholder="Email Address"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </div>
                <div className="field">
                  <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </div>
                <div className="field">
                  <input
                    type="password"
                    placeholder="Confirm Password"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    required
                  />
                </div>
                <div className="field btn">
                  <input type="submit" value="Signup" />
                </div>
              </form>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Auth;
