import { createBrowserRouter, RouterProvider, Outlet } from "react-router-dom";
import React, { useState } from "react";
import ReactDOM from "react-dom/client";
import Auth from "./components/Auth";
import Header from "./components/Header";
import Body from "./components/Body";

const AppLayout = ({ openAuthModal }) => {
  return (
    <div className="app">
      <Header openAuthModal={openAuthModal} />
      <Outlet />
    </div>
  );
};

const App = () => {
  const [isAuthVisible, setIsAuthVisible] = useState(false); // State to control modal visibility
  const [isSignup, setIsSignup] = useState(false); // State to toggle between login/signup

  const openAuthModal = (signup = false) => {
    setIsSignup(signup);
    setIsAuthVisible(true);
  };

  const closeAuthModal = () => {
    setIsAuthVisible(false);
  };

  const appRouter = createBrowserRouter([
    {
      path: "/",
      element: <AppLayout openAuthModal={openAuthModal} />,
      children: [
        {
          path: "/",
          element: <Body />
        }
      ]
    }
  ]);

  return (
    <div>
      <RouterProvider router={appRouter} />
      {isAuthVisible && <Auth onClose={closeAuthModal} isSignup={isSignup} />}
    </div>
  );
};

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App />);
