import { createBrowserRouter, RouterProvider, Outlet } from "react-router-dom";
import React, { useState } from "react";
import ReactDOM from "react-dom/client";
import Auth from './components/Auth.js';
import Header from './components/Header.js';
import Body from "./components/Body.js";

export const AppLayout = () => {
  return (
    <div className="app">
      <Header />
      <Outlet />
    </div>
  );
};

const App = () => {
  const [isAuthVisible, setIsAuthVisible] = useState(false); // State to control the modal visibility

  const openAuthModal = () => {
    setIsAuthVisible(true);
  };

  const closeAuthModal = () => {
    setIsAuthVisible(false);
  };

  return (
    <div>
      <button onClick={openAuthModal}>Login/Signup</button>

      {/* Render Auth component as a modal */}
      {isAuthVisible && <Auth onClose={closeAuthModal} />}
    </div>
  );
};

const appRouter = createBrowserRouter([
  {
    path: "/",
    element: <AppLayout />,
    children: [
      {
        path: "/",
        element: <Body />
      },
      {
        path: "/auth/login",
        element: <Auth />
      },
      {
        path: "/auth/signup",
        element: <Auth />
      },
    ]
  }
]);

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<RouterProvider router={appRouter} />);
